  const router = useRouter();

  // router参数的接收
  // console.log('router.query...................', router.query);

  const fn = () => {
    router.push({
      pathname: "/about",
    });
  };

  return (
    <div>
      {" "}
      {/* 第一种 */}{" "}
      {/* <Link href={{ pathname: "./about", query: { name: "456" } }}>
              <a>about</a>
            </Link> */}{" "}
      <h2> Hello World </h2> {/* 第二种js编程跳转 */}{" "}
      <button onClick={fn}> useRouter </button> <img src="980.jpg" />
    </div>
  );
};

export default Index;
